class Point {
  #x;
  #y;
  constructor(x, y) {
    this.#x = x;
    this.#y = y;
  }
  get x() { return this.#x; }
  get y() { return this.#y; }
  static equaler = {
    equals(a, b) {
      return #x in a && #x in b && a.#x == b.#x && a.#y == b.#y;
    },
    hash(obj) {
      let hc = Object.hash(obj.#x);
      hc = ((hc << 7) | (hc >>> 25)) ^ Object.hash(obj.#y);
      return hc;
    }
  }
}
