```js
function Task(init, opts) {
  if (opts?.delay) {
    const { promise, resolve, reject } = Promise.withResolvers();
    promise.start = () => init(resolve, reject);
    return promise;
  }  
  const p = new Promise(init);
  p.start = () => {};
  return p;
}
